import React, { Component } from 'react'
import axios from 'axios'

export default class NotesList extends Component {

    state = {
        notes: []
    }

    async componentDidMount(){
        let resp = await axios.get("http://localhost:4000/api/notes");
        this.setState({
            notes : resp.data
        })

        console.log(this.state.notes);
    }

    render() {
        return (
            <div className="row">
                {
                    this.state.notes.map(note=>
                        <div className="col-md-3" key={note._id}>
                            <div className="card">
                                <div className="card-header">{note.tittle}</div>
                                <div className="card-body">{note.description}</div>
                                <div className="card-footer">{note.date}</div>
                            </div>
                        </div>
                    )
                }
                
            </div>
        )
    }
}
